<template>
  <van-popup
    v-model="showModel"
    position="bottom"
    :style="{ height: '100%' }"
    @close="onClosePopup"
  >
    <div class="m-signature-pad">
      <div class="m-signature-pad--body">
        <canvas id="m-signature-canvas"></canvas>
      </div>
      <div class="m-signature-pad--footer">
        <div class="left">
          <van-button type="primary" size="small" @click="clear">清除</van-button>
        </div>
        <div class="right">
          <van-button type="info" size="small" @click="save">确定</van-button>
          <van-button type="defalut" size="small" @click="onClosePopup">退出</van-button>
        </div>
      </div>
    </div>
  </van-popup>

</template>

<script>
import * as dd from 'dingtalk-jsapi'
/**
 * 手签组件
 */
export default {
  name: 'Signature',
  components: {

  },
  props: {
    showPopup: {
      type: Boolean,
      default: false,
      required: true
    }
  },
  data () {
    return {
      showModel: false,
      signaturePad: null,
      canvas: null
    }
  },
  watch: {
    showPopup (value, oldValue) {
      if (value !== oldValue) {
        this.showModel = value
        if (this.showModel && !this.canvas) {
          this.$nextTick(() => {
            this.setlandscape()
            this.initCanvas()
          })
        }
      }
    }
  },
  created () {
    this.init()
  },
  mounted () {
    this.setlandscape()
    this.initCanvas()
  },
  methods: {
    init () {
      this.showModel = this.showPopup
    },
    initCanvas () {
      function resizeCanvas () {
        // When zoomed out to less than 100%, for some very strange reason,
        // some browsers report devicePixelRatio as less than 1
        // and only part of the canvas is cleared then.
        var ratio = Math.max(window.devicePixelRatio || 1, 1)
        canvas.width = canvas.offsetWidth * ratio
        canvas.height = canvas.offsetHeight * ratio
        canvas.getContext('2d').scale(ratio, ratio)
      }
      var canvas = document.getElementById('m-signature-canvas')
      if (canvas) {
        window.onresize = resizeCanvas
        resizeCanvas()
        this.canvas = canvas
        // eslint-disable-next-line no-undef
        this.signaturePad = new SignaturePad(canvas)
        this.clear()
      }
    },
    clear () {
      if (this.signaturePad) {
        this.signaturePad.clear()
      }
    },
    /**
     * 设置横屏
     */
    setlandscape () {
      dd.device.screen.rotateView({
        showStatusBar: true, // 否显示statusbar
        clockwise: true, // 是否顺时针方向
        onSuccess: function (result) {
        },
        onFail: function (err) {
          console.log(err)
        }
      })
    },
    /**
     * 重置页面
     */
    resetView () {
      dd.device.screen.resetView({
        onSuccess: function (result) {
        },
        onFail: function (err) {
          console.log(err)
        }
      })
    },
    onClosePopup () {
      this.resetView()
      this.$emit('onClosePopup')
    },

    save () {
      if (this.signaturePad) {
        var signaturePad = this.signaturePad
        // eslint-disable-next-line no-unused-vars
        var canvas = this.canvas
        if (signaturePad.isEmpty()) {
          this.$toast('请提供签名')
        } else {
          // var ctx = canvas.getContext('2d')
          // var imgData = ctx.getImageData(0, 0, canvas.width, canvas.height).data

          // var lOffset = canvas.width; var rOffset = 0; var tOffset = canvas.height; var bOffset = 0

          // for (var i = 0; i < canvas.width; i++) {
          //   for (var j = 0; j < canvas.height; j++) {
          //     var pos = (i + canvas.width * j) * 4
          //     if (imgData[pos] > 0 || imgData[pos + 1] > 0 || imgData[pos + 2] || imgData[pos + 3] > 0) {
          //       bOffset = Math.max(j, bOffset) // 找到有色彩的最底部的纵坐标
          //       rOffset = Math.max(i, rOffset) // 找到有色彩的最右端

          //       tOffset = Math.min(j, tOffset) // 找到有色彩的最上端
          //       lOffset = Math.min(i, lOffset) // 找到有色彩的最左端
          //     }
          //   }
          // }
          // // 由于循环是从0开始的,而我们认为的行列是从1开始的
          // lOffset++
          // rOffset++
          // tOffset++
          // bOffset++
          // // console.log(lOffset, rOffset, tOffset, bOffset)
          // var oldUrl = signaturePad.toDataURL()
          // var originImage = new Image()
          // originImage.src = oldUrl

          // var canvas2 = document.createElement('canvas')
          // canvas2.width = rOffset - lOffset
          // canvas2.height = bOffset - tOffset
          // document.body.appendChild(canvas2)

          // // 用9参数的drawImage方法对图片进行裁减
          // canvas2.getContext('2d').drawImage(originImage, lOffset, tOffset, rOffset - lOffset, bOffset - tOffset, 0, 0, rOffset - lOffset, bOffset - tOffset)
          // // var newUrl = canvas2.toDataURL()
          // // var newImage = new Image()
          // // newImage.src = newUrl
          // var result = canvas2.toDataURL()
          // console.log('result', signaturePad.toDataURL())
          // // window.open(canvas2.toDataURL())// 数据格式为"data:image/png;base64,"
          // canvas2.remove()
          var result = signaturePad.toDataURL()
          this.resetView()
          this.$emit('onPopupConfirm', result)
        }
      }
    }
  }
}
</script>

